package racingcar.controller;import racingcar.domain.CarGameManager;import racingcar.view.InputView;public class CarGameController {    private final InputView inputView;    private CarGameManager carGameManager;    public CarGameController() {        this.inputView = new InputView();    }    public void start() {        try {            String inputCarNames = inputView.readCarName();            carGameManager = CarGameManager.initCarGame(inputCarNames);        } catch (IllegalStateException e) {            start();        }    }}