package racingcar.controller;import racingcar.domain.CarGameManager;import racingcar.view.InputView;import racingcar.view.OutputView;public class CarGameController {    private final InputView inputView;    private final OutputView outputView;    private CarGameManager carGameManager;    public CarGameController() {        this.inputView = new InputView();        this.outputView = new OutputView();    }    public void start() {        try {            String inputCarNames = inputView.readCarName();            carGameManager = CarGameManager.initCarGame(inputCarNames);        } catch (IllegalArgumentException e) {            outputView.printError(e.getMessage());            start();        }    }}