package racingcar.domain;import java.util.ArrayList;import java.util.Arrays;import java.util.List;import java.util.stream.Collectors;public class CarGameManager {    private final List<Car> cars = new ArrayList<>();    private CarGameManager() {    }    public static CarGameManager initCarGame(String carNames) {        return new CarGameManager();    }    private void addCar(String inputCarNames) {        List<String> carNames = carNameSeparator(inputCarNames);        carNames.stream().forEach(name -> cars.add(new Car(name)));    }    private List<String> carNameSeparator(String carNames) {        List<String> convert = Arrays.stream(carNames.split(","))            .collect(Collectors.toCollection(ArrayList::new));        return convert;    }}