package racingcar.exception;public enum ExceptionMessage {    ERROR_MESSAGE_PREFIX("[ERROR]"),    INPUT_CAR_NAME_ERROR(" 자동차 이름 입력이 잘못 되었습니다."),    INPUT_TRY_COUNT_ERROR(" 횟수 입력이 잘못 되었습니다.");    private final String message;    ExceptionMessage(String message) {        this.message = message;    }    public String getErrorMessage() {        return ERROR_MESSAGE_PREFIX.message + this.message;    }}