package racingcar.domain;import org.assertj.core.api.Assertions;import org.junit.jupiter.api.DisplayName;import org.junit.jupiter.api.Test;class CarGameManagerTest {    @Test    @DisplayName("입력받은 이름들로 자동차들이 추가 되는지 확인하는 테스트")    public void addCarTest() {        String inputCarNames = "pobi,woni";        CarGameManager carGame = CarGameManager.initCarGame(inputCarNames);        Assertions.assertThat(carGame)            .extracting("cars")            .asList().extracting("name")            .containsExactly("pobi", "woni");    }}