package racingcar.domain;import org.assertj.core.api.Assertions;import org.junit.jupiter.api.DisplayName;import org.junit.jupiter.api.Test;class CarTest {    @Test    @DisplayName("자동차 전진 테스트")    void forwardTest() {        Car car = new Car("pobi");        car.forward(6);        Assertions.assertThat(car).extracting("position")            .isEqualTo(1);    }    @Test    @DisplayName("자동차 출력 테스트")    void carPrintTest() {        Car car = new Car("pobi");        car.forward(6);        car.forward(7);        String path = car.toString();        Assertions.assertThat(path).isEqualTo("---");    }}